import React from 'react'
// import { Link } from 'react-router-dom'
import style from '../css/Paginado.module.css'
import { useSelector } from "react-redux"

function Paginado({videoGamePerPage,allVideoGames, paginado,currentPage}) {
  let pageNumbers = [];

  for(let i = 1 ; i <= Math.ceil(allVideoGames/videoGamePerPage); i++ ){  //math.ceil redondea para arriba.
    pageNumbers.push(i)
  }

  return (
    <nav>
      <ul className={style.ul}>
      {
        currentPage - 1 > 0 ? (
          <button onClick={() => paginado(currentPage - 1)} >Prev</button>
        )
        : null
      }
      { 
        pageNumbers && pageNumbers.map(number => (
          <li className={style.li} key={number}>
            <button className={style.botonPaginado} onClick={() => paginado(number)}>{number}</button>
          </li>
        ))
      }
      {
        currentPage + 1
      }
      </ul>
    </nav>
  )
}

export default Paginado
