import React from 'react'
import { useState } from 'react'
import { getNameVideogames } from '../Redux/actions/index'
import { useDispatch } from 'react-redux'
import style from "../css/SerchBar.module.css"

const validate = (input) => {
  let errors = {};
  if(!input.name){
    errors.name = "this field is required"
  }
  return errors
}

function SerchBar() {
  const [input , setInput] = useState('')
  const dispatch = useDispatch()
  const [errors, setErrors] = useState({})

  const handleInputChange = (e) => {
    e.preventDefault()
    setInput(e.target.value)
    setErrors(validate({
      ...input,
      [e.target.name]: e.target.value
    }))
    console.log
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    if(!input.trim()){
      return alert("You need to put a name")
    }else{  
      dispatch(getNameVideogames(input))
      setInput("")
    }
  }

  return (
    <div className={style.searchbar}>
      <form className='form'>
        <input className={style.input} 
        name="name"
        type="text"
        placeholder = "Buscar videogame..."
        value={input}
        onChange = {handleInputChange}
        />
      {errors.name && (
        <p className={style.errors}>{errors.name}</p>
      )}
      <button className= {style.button}  onSubmit={handleSubmit} type='submit'>SEARCH</button>
      </form>
    </div>
  )
}

export default SerchBar
