import React from 'react'
import { useState } from 'react'
import { getNameVideogames,flagLoad } from '../Redux/actions/index'
import { useDispatch } from 'react-redux'
import style from "../css/SerchBar.module.css"
import Loading from "./Loading"

function SerchBar() {
  const [input , setInput] = useState('')
  const dispatch = useDispatch()

  const handleInputChange = (e) => {
    e.preventDefault()
    setInput(e.target.value)
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    if(!input.trim()){
      return alert("You need to put a name")
    }else{  
      dispatch(getNameVideogames(input))
      setInput("")
      dispatch(flagLoad(true))
    }
  }

  return (
    <div className={style.searchbar}>
      <form className='form' onSubmit={handleSubmit}>
        <input className={style.input} 
        type="text"
        placeholder = "Buscar videogame..."
        value={input}
        onChange = {handleInputChange}
        />
      <button className= {style.button} type='submit'>Buscar</button>
      </form>
    </div>
  )
}

export default SerchBar
