import React from 'react'
import { getVideogames } from '../Redux/actions'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import Card from './Card'

function Home() {

  const dispatch = useDispatch()
  const allVideoGames = useSelector(state => state.videogames) // Me traigo del state global de redux los videojuegos, igual que hacer el mapStatetoProps. 
  
  useEffect(() => {
    dispatch(getVideogames())
  },[])

  const handleClick = (e) => {
    e.preventDefault();
    dispatch(getVideogames())
  }

  return (
    <div className='Home'>
        <Link to= '/videogame'>
          <button>Crear Videojuego</button>
        </Link>
        <h1>PolloGames</h1>
        <button onClick= {handleClick}>Recargar videogames</button>
        <div>
          <select>
            <option value='Asc'>Ascendente</option>
            <option value='Desc'>Descendente</option>
          </select>

          <select>
            <option value='genre'>Genre</option>
          </select>

          <select>
            <option value='videogame'>Videogame</option>
            <option value='api'>Videogame Existente</option>
          </select>

          {
            allVideoGames?.map((e) => {
              return (
              <fragment className = 'cartas'>
                      <div key={e.id}>
                  <Link to={"/home/" + e.id}>
                    <Card
                      name={e.name}
                      image={e.image}
                      genres={e.genres}
                      rating={e.rating}
                    />
                  </Link>
                </div>
              </fragment>
              )})
          }
        </div>
    </div>
  )
}

export default Home
