import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useEffect } from 'react'
import { Link, useParams } from 'react-router-dom'
import { getDetail } from '../Redux/actions'

function Detail() {
  const dispatch = useDispatch()
  const { id } = useParams()
  const myVideogame = useSelector(state => state.details)

  useEffect(() => {
    dispatch(getDetail(id))
  },[dispatch])
  console.log(myVideogame)

// genres: (3) ['Action', 'Adventure', 'RPG']
// id: 3328
// image: "https://media.rawg.io/media/games/618/618c2031a07bbff6b4f611f10b6bcdbc.jpg"
// name: "The Witcher 3: Wild Hunt"
// platforms: (6) ['Nintendo Switch', 'PlayStation 5', 'Xbox Series S/X', 'Xbox One', 'PC', 'PlayStation 4']
// rating: 4.67
// released: "2015-05-18"

  return (
    <div className='fondo-detail'>
      { myVideogame ? (
        <div className='contenedor-general'>
          <h1 className='titulo-game'>{myVideogame.name}</h1>
          <img className='img-detail' src={myVideogame.image} />

          <div className='contenedor-secundario'>
            <h4>üèÜRating: {myVideogame.rating} </h4>
            <h4>Released: {myVideogame.released}</h4>
            <h4>Platform: {myVideogame.platforms}</h4>
            <h4>
              Genres:
              {myVideogame.genres?.map((g) => g.name).join(", ")}
            </h4>
            <h4>Description: {}</h4>
          </div>
        </div>
      ): null};
    </div>
  )
}

export default Detail
