import React from 'react'
// import { Link } from 'react-router-dom'
import style from '../css/Paginado.module.css'
import { useSelector } from "react-redux"

function Paginado({videoGamePerPage,allVideoGames, paginado,nextPage,}) {
  let pageNumbers = [];

  for(let i = 1 ; i <= Math.ceil(allVideoGames/videoGamePerPage); i++ ){  //math.ceil redondea para arriba.
    pageNumbers.push(i)
  }
  const currentPage = useSelector(state => state.currentPage)

  return (
    <nav>
      <ul className={style.ul}>
      {/* {
        indexOfFirstVideogame - 1 > 0 ? (
          <button onClick={() => paginado()} >Prev</button>
        )
        : null
      } */}
      { 
        pageNumbers && pageNumbers.map(number => (
          <li className={style.li} key={number}>
            <button className={style.botonPaginado} onClick={() => paginado(number)}>{number}</button>
          </li>
        ))
      }
      </ul>
    </nav>
  )
}

export default Paginado
