import React from 'react'
import { getVideogames } from '../Redux/actions'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import Card from './Card'

function Home() {

  const dispatch = useDispatch()
  const allVideoGames = useSelector(state => state.videogames) // Me traigo del state global de redux los videojuegos, igual que hacer el mapStatetoProps. 
  
  useEffect(() => {
    dispatch(getVideogames())
  },[dispatch])

  function handleClick (e){
    e.preventDefault();
    dispatch(getVideogames())
  }

  return (
    <div className='Home'>
        <Link to= '/videogame'>
          <botton>Crear Videojuego</botton>
        </Link>
        <h1>PolloGames</h1>
        <div>
          <botton>Recargar videogames</botton>
        </div>
        <div>
          <select>
            <option value='Asc'>Ascendente</option>
            <option value='Desc'>Descendente</option>
          </select>

          <select>
            <option value='genre'>Genre</option>
          </select>

          <select>
            <option value='videogame'>Videogame</option>
            <option value='api'>Videogame Existente</option>
          </select>

          {
            allVideoGames?.map((e) => {
              return (
              <fragment className = 'cartas'>
                <Link to={'/home/'+ e.id}>
                <Card 
                name = {e.name}
                image = {e.background_image}
                genre = {e.genre}
                rating = {e.rating}
                />
                </Link>
              </fragment>
              )})
          };
        </div>
    </div>
  )
}

export default Home
