import React from 'react'
import { useState } from 'react'
import { getNameVideogames } from '../Redux/actions/index'
import { useDispatch } from 'react-redux'
import style from "../css/SerchBar.module.css"

const validate = (input) => {
  let errors = {};
  if(!input.name){
    errors.name = "this field is required"
  }
  return errors
}

function SerchBar() {
  const [input , setInput] = useState('')
  const dispatch = useDispatch()
  const [errors, setErrors] = useState({})
  const re = /^[0-9a-zA-ZÁ-ÿ.:-\s]{0,40}$/;

  //!re.exec(e.target.value)
  const handleInputChange = (e) => {
    e.preventDefault()
    setInput(e.target.value)
    if(!re.exec(e.target.value)){
      
      console.log(e.target.value)
    }
    // setErrors(validate({
    //   ...input,
    //   [e.target.name]: e.target.value
    // }))
    
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    if(!input.trim()){
      return alert("You need to put a name")
    }else{  
      dispatch(getNameVideogames(input))
      setInput("")
    }
  }

  return (
    <div className={style.searchbar}>
      <form >
        <div>
        <input className={style.input} 
        name="name"
        type="text"
        placeholder = "Buscar videogame..."
        value={input}
        onChange = {handleInputChange}
        />
        {errors.name && (
        <div className={style.errors}>
        <p>{errors.name}</p>
        </div>
       )}
        <button className= {style.button}  onSubmit={handleSubmit} type='submit'>SEARCH</button>
        </div>
      
      </form>
    </div>
  )
}

export default SerchBar
