import React from 'react'
import { useState , useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useHistory, Link } from 'react-router-dom'
import { postVideogames , getGenre , getVideogames } from '../Redux/actions'

function VideoGameCreated() {
  const dispatch = useDispatch()
  const history = useHistory()
  const allVideogames = useSelector(state => state.videogames)
  const genres = useSelector(state => state.genres)
  const platforms = useSelector(state => state.platforms)
  const [input , setInput] = useState({
     name: "",
     image: "",
     description: "",
     released: "",
     rating: "",
     genres: [],
     platforms: [],
  });

  useEffect(() => {
    dispatch(getGenre())
    dispatch(getVideogames())
  },[]);

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name] : e.target.value
    });
    console.log(input)
  }

  const handleSelect = (e) => {
    setInput({
      ...input,
      genres: [...input.genres, e.target.value],
      platforms: [...input.platforms, e.target.value]
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    dispatch(postVideogames(input))
    alert("Successfully created")
    setInput({
      name: "",
      image: "",
      description: "",
      released: "",
      rating: "",
      genres: [],
      platforms: [],
    })
    history()
  }

  return (
    <div>
      <Link to="/home">
        <button className='buton' >Go back home</button>
      </Link>
      <h1>Create your video game</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre: </label>
          <br></br>
            <input 
            type= "text"
            value= {input.name}
            name = "name"
            onChange={handleChange}
            />
        </div>
        <div>
          <label>Imagen: </label>
          <br></br>
            <input 
            type= "text"
            value= {input.image}
            name = "image"
            onChange={handleChange}
            />
        </div>

        <div>
          <label>Lanzamiento:</label>
          <br></br>
            <input 
              type= "date"
              value= {input.released}
              name = "released"
              onChange={handleChange}
              />
        </div>

        <div>
          <label>Rating</label>
          <br></br>
            <input 
              type= "number"
              value= {input.rating}
              name = "rating"
              onChange={handleChange}
              />
        </div>

        <div>
          <label>Description</label>
          <br></br>
            <input 
              type= "text"
              value= {input.description}
              name = "description"
              onChange={handleChange}
              />
        </div>

        <div>
          <label>Generos</label>
          <br></br>
          <select 
            className='input'
            defaultValue="select"
            onChange = {handleSelect}
            >
            <option className='select' disabled>Select</option>
            {
              genres?.map((e) => {
                return (
                <option className='select' value={e.name} key={e.id}>{e.name}</option>
                )})
            }
            </select>
        </div>  

        <div>
          <label>Platforms</label>
          <br></br>
          <select 
            className='select'
            defaultValue="platforms"
            onChange = {handleSelect}
            >
            <option className='select' disabled>Platforms</option>
            {
              platforms?.map((e) => {
                return (
                <option className='select' value={e} key={e}>{e}</option>
                )})
            }
            </select>
        </div>  
        <br></br>
        <button type='submit'>Create videogame</button>
      </form>
    </div>
  )
}

export default VideoGameCreated
