import { GET_VIDEOGAMES,
  GET_GENRE,GET_DETAIL,
  FILTER_BY_GENRES,
  FILTER_CREATED,
  ORDER_BY_NAME,
  ORDER_BY_RATING,
  GET_NAME_VIDEOGAMES,
  GET_LOADER,
  VACIAR_DETAIL
} from '../constantes';

const initialState = {
   videogames: [],
   genres: [],
   allVideogames: [],
}

function rootReducer(state= initialState, action) { 
  switch(action.type){
    case GET_VIDEOGAMES: 
      return {
        ...state, 
        videogames: action.payload,
        allVideogames: action.payload
      }
    case GET_GENRE:
      return {
        ...state,
        genres: action.payload
      }
    case FILTER_BY_GENRES: 
      const allvideogames = state.videogames
      const filtergenre = action.payload === "All" 
      ? allvideogames 
      : allvideogames.filter((e) => e.genres === action.payload)

      return {
        ...state,
        videogames: filtergenre
      }

      default: 
        return state
  }
}

export default rootReducer
