import React from 'react'
import { useState , useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate, Link } from 'react-router-dom'
import { postVideogame , getGenre , getVideogames } from '../Redux/actions'

function Validaciones (){
  const videogames = useSelector((state) => state.videogames);

  if(!input.name.trim()){
    return alert("Need to put name")
  }else if(
    videogames.find(e => {
      e.name.toLowerCase().trim() === input.name.toLowerCase().trim()
    })
  ){return alert(`The name ${input.name} already exist`);}
  
}

function VideoGameCreated() {
  const dispatch = useDispatch()
  const navigate = useNavigate()
  const genres = useSelector(state => state.genres)
  const platforms = useSelector(state => state.platforms)
  const [input , setInput] = useState({
     name: "",
     image: "",
     description: "",
     released: "",
     rating: "",
     genres: [],
     platforms: [],
  });

  useEffect(() => {
    dispatch(getGenre())
    dispatch(getVideogames())
  },[dispatch]);

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name] : e.target.value  //va a setearse dependiendo del target que este en ese momento. 
    });
    console.log(input)
  }

  const handleSelect = (e) => {
    setInput({
      ...input,
      genres: input.genres.includes(e.target.value) 
      ? input.genres 
      : [...input.genres, e.target.value]
    })
  }

  const handleSelect2 = (e) => {
    setInput({
      ...input,
      platforms: input.platforms.includes(e.target.value) 
      ? input.platforms 
      : [...input.platforms, e.target.value]
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    dispatch(postVideogame(input))
    alert("Successfully created")
    setInput({
      name: "",
      image: "",
      description: "",
      released: "",
      rating: "",
      genres: [],
      platforms: [],
    })
    navigate("/home")  //redirige a la ruta que le pase. En router v6 reemplaza al hook useHistory. 
  }

  const handleDelete1 = (e) => {
    setInput({
      ...input,
      genres: input.genres.filter((el) => el !== e)
    })
  }
  const handleDelete2 = (e) => {
    setInput({
      ...input,
      platforms: input.platforms.filter((el) => el !== e)
    })
  }

  return (
    <div>
      <Link to="/home">
        <button className='buton' >Go back home</button>
      </Link>
      <h1>Create your video game</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre: </label>
          <br></br>
            <input 
            type= "text"
            value= {input.name}
            name = "name"
            onChange={handleChange}
            />
        </div>
        <div>
          <label>Imagen: </label>
          <br></br>
            <input 
            type= "text"
            value= {input.image}
            name = "image"
            onChange={handleChange}
            />
        </div>

        <div>
          <label>Lanzamiento:</label>
          <br></br>
            <input 
              type= "date"
              value= {input.released}
              name = "released"
              onChange={handleChange}
              />
        </div>

        <div>
          <label>Rating</label>
          <br></br>
            <input 
              type= "number"
              value= {input.rating}
              name = "rating"
              onChange={handleChange}
              />
        </div>

        <div>
          <label>Description</label>
          <br></br>
            <input 
              type= "text"
              value= {input.description}
              name = "description"
              onChange={handleChange}
              />
        </div>

        <div>
          <label>Generos</label>
          <br></br>
          <select 
            className='input'
            defaultValue="select"
            onChange = {handleSelect}
            >
            <option className='select' disabled>Select</option>
            {
              genres?.map((e) => {
                return (
                <option className='select' value={e.name} key={e.id}>{e.name}</option>
                )})
            }
            </select>
            
            <ul className="ul">
                {input.genres.map((e) => (
                  <li key={e} className="listaGP">
                    <div className="divGP">
                      {e + " "}
                      <button type='button' onClick={() => handleDelete1(e)}>
                        X
                      </button>
                    </div>
                  </li>
                ))}
            </ul>
        </div>  

        <div>
          <label>Platforms</label>
            <br></br>
              <select 
                className='select'
                defaultValue="platforms"
                onChange = {handleSelect2}
                >
                <option className='select' disabled>Platforms</option>
                {
                  platforms?.map((e) => {
                    return (
                    <option className='select' value={e} key={e}>{e}</option>
                    )})
                }
            </select>
            <ul className="ul">
                {input.platforms.map((e) => (
                  <li key={e} className="listaGP">
                    <div className="divGP">
                      {e + " "}
                      <button type='button' onClick={() => handleDelete2(e)}>
                        X
                      </button>
                    </div>
                  </li>
                ))}
            </ul>
        </div>  
        <br></br>
        <button type='submit'>Create videogame</button>
      </form>
    </div>
  )
}

export default VideoGameCreated
